export const getLocations = async (token) => {
  const res = (
    await (
      await fetch(
        `${DIOM_BASED_URLS}/admin-business-locations?filter={"where":{"visibility":true}}`,
        {
          method: "GET",
          redirect: "follow",
          headers: {
            Authorization: "Bearer " + token,
          },
        }
      )
    ).json()
  ).map((element) => ({
    value: element.id,
    label: element.Name,
  }));
  return [
    {
      label: "--All Locations",
      options: [...res],
    },
  ];
};
