import { DIOM_BASED_URLS } from "../config/url";
export const getreportLocations = async (
  token,
  locationApplyFilter,
  reportFinalValues
) => {
  console.log("locationApplyFilter====>  ", locationApplyFilter);
  console.log("reportFinalValues ====^^^ : ", reportFinalValues?.diomBrand);
  console.log(
    `/admin-business-locations?filter={"where":{"locationCategoriesId":{"inq": ${reportFinalValues?.diomBrand}}}}`
  );
  // const diomBrandValue = reportFinalValues?.diomBrand;
  const res = (
    await (
      await fetch(
        !locationApplyFilter
          ? `${DIOM_BASED_URLS}/admin-business-locations?filter={"where":{"visibility":true}}`
          : `${DIOM_BASED_URLS}/admin-business-locations?filter={"where":{"locationCategoriesId":{"inq": ${reportFinalValues?.diomBrand}}}}`,
        {
          method: "GET",
          redirect: "follow",
          headers: {
            Authorization: "Bearer " + token,
          },
        }
      )
    ).json()
  ).map((element) => ({
    value: element.id,
    label: element.Name,
  }));
  return [
    {
      label: "--All Locations",
      options: [...res],
    },
  ];
};

export const getreportLocationBrand = async (token) => {
  const res = (
    await (
      await fetch(`${DIOM_BASED_URLS}/admin-location-categories`, {
        method: "GET",
        redirect: "follow",
        headers: {
          Authorization: "Bearer " + token,
        },
      })
    ).json()
  ).map((element) => ({
    value: element.id,
    label: element.name,
  }));
  return [
    {
      label: "--All Brands",
      options: [...res],
    },
  ];
};

