import { DIOM_BASED_URLS } from "../config/url";

export const getreportLocations = async (
  token,
  locationApplyFilter,
  reportFinalValues,
  locationInvalidatequerry
) => {
  // console.log("locationApplyFilter====>  ", locationApplyFilter);
  // console.log(
  //   "reportFinalValues ====^^^ : ",
  //   JSON.stringify(reportFinalValues?.diomBrand)
  // );

  const diomBrandValue = JSON.stringify(reportFinalValues?.diomBrand);
  // const dd = JSON.parse(reportFinalValues?.diomBrand);

  console.log(
    "data after parse ===> ",
    diomBrandValue ? diomBrandValue[0] : diomBrandValue
  );

  const res = (
    await (
      await fetch(
        !locationApplyFilter || diomBrandValue.length == 0
          ? `${DIOM_BASED_URLS}/admin-business-locations?filter={"where":{"visibility":true}}`
          : `${DIOM_BASED_URLS}/admin-business-locations?filter={"where":{"locationCategoriesId":{"inq":${diomBrandValue}}}}`,
        {
          method: "GET",
          redirect: "follow",
          headers: {
            Authorization: "Bearer " + token,
          },
        }
      )
    ).json()
  ).map((element) => ({
    value: element.id,
    label: element.Name,
  }));
  return [
    {
      label: "--All Locations",
      options: [...res],
    },
  ];
};

export const getreportLocationBrand = async (token) => {
  const res = (
    await (
      await fetch(`${DIOM_BASED_URLS}/admin-location-categories`, {
        method: "GET",
        redirect: "follow",
        headers: {
          Authorization: "Bearer " + token,
        },
      })
    ).json()
  ).map((element) => ({
    value: element.id,
    label: element.name,
  }));
  return [
    {
      label: "--All Brands",
      options: [...res],
    },
  ];
};

