/**
 *
 * @param size
 * @param page
 * @param {JWT} token
 * @returns a list of resourcetype dropdown in DIOM
 */

export const getResourcetypereports = async (
  token = "",
  resourcetypeApplyFilter,
  reportFinalValues
) => {
  const diomLocationValue = JSON.stringify(reportFinalValues?.diomLocations);

  return await (
    await fetch(
      !resourcetypeApplyFilter
        ? `${DIOM_BASED_URLS}/admin-resource-types-inventories`
        : `${DIOM_BASED_URLS}/admin-resource-types-inventories?filter={"where":{"BusinessId":{"inq":${diomLocationValue}}}}`,
      {
        method: "GET",
        redirect: "follow",
        headers: {
          Authorization: "Bearer " + token,
        },
      }
    )
  ).json();
};
