import React, { useState, useEffect } from "react";
import "./css/CheckedinCustomerDetail.css";
import { Link } from "react-router-dom";
import "react-toastify/dist/ReactToastify.css";
import BounceLoader from "react-spinners/BounceLoader";
import ImageAlt from "./imgs/transparentImage.png";
import {
  Row,
  Col,
  TabContent,
  NavLink,
  NavItem,
  Nav,
  TabPane,
  Button,
  Card,
  CardBody,
  ButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
} from "reactstrap";
import classnames from "classnames";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory, {
  PaginationProvider,
  PaginationListStandalone,
  SizePerPageDropdownStandalone,
} from "react-bootstrap-table2-paginator";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import "../../Tables/datatables.scss";
import { useUserProfileDetail } from "./useUserProfileDetail";
const activeTabJustify = "1";
const { SearchBar } = Search;
const columns = [
  {
    dataField: "id",
    text: "No.",
    sort: true,
  },
  {
    dataField: "_id",
    text: "Booking ID",
    sort: true,
    formatter: (cell, row) => (
      <Link
        to={`/bookingdetail/${row._id}/${activeTabJustify}`}
        className="link"
      >
        {cell}
      </Link>
    ),
  },
  {
    dataField: "resourceName",
    text: "Resource",
    sort: true,
  },
  {
    dataField: "businessName",
    text: "Location",
    sort: true,
  },
  {
    dataField: "createdAt",
    text: "Booking Date",
    sort: true,
  },
  {
    dataField: "bookingType",
    text: "Booking Type",
    sort: true,
  },
  {
    dataField: "invoiceNumber",
    text: "Invoice ID",
    sort: true,
    formatter: (cell, row) => (
      <Link to={`/invoicesdetailpage/${row.invoiceNumber}`} className="link">
        {" "}
        {cell}{" "}
      </Link>
    ),
  },
  {
    dataField: "totalAmount",
    text: "Total Paid",
    sort: true,
  },
];
const defaultSorted = [
  {
    dataField: "id",
    order: "asc",
  },
];

const UserProfileDetail = () => {
  const {
    currentPage,
    pageOptions,
    userdetailsData,
    toggleCustomJustified,
    activeTabJustify, setActiveTabJustify,
    userActivebookingsData,
    userPastBookingsData,
    userScheduledBookingsData,
    hasNextPage,
    hasPreviousPage,
    total,
    pastHasNextPage,scheduledHasNextPage,
    isLoading,
    pagelengthnum,
    pageSize,
    toggle,
    isOpen,

    changeCurrentPage,
  } = useUserProfileDetail();

  // const [userActiveBookings, setUserActiveBookings] = useState([
  //   userActivebookingsData,
  // ]);
  // const [activeTabJustify, setActiveTabJustify] = useState("1");

  // const pageOptions = {
  //   sizePerPage: 10,
  //   totalSize: userActiveBookings.length, // replace later with size(customers),
  //   custom: true,
  // };

  // const toggleCustomJustified = (tab) => {
  //   if (activeTabJustify !== tab) {
  //     setActiveTabJustify(tab);
  //   }
  // };
  const Loader = require("react-loader");

  return (
    <>
      {isLoading ? (
        <Loader loaded={false} className="spinner" />
      ) : (
        <div className="page-content">
          <div>
            <Row className="mb-4">
              <Col md={4}>
                <Link to="/userprofile" className="link">
                  {" "}
                  <span className="fas fa-angle-left arrowheightwidth"></span>
                </Link>
                <span className="profiletitle ">Profile Details</span>
              </Col>
              <Col md={6}></Col>
              <Col md={2}></Col>
            </Row>
          </div>
          <Row>
            <Col md={12} lg={12} sm={12}>
              <div>
                <Card>
                  <Row className="mt-2 mb-2">
                    <Col md={3} lg={3} sm={4}>
                      <div className="checkedinimgdiv">
                        <img
                          src={
                            // (userdetailsData?.imageLink)
                            //   ? (userdetailsData?.imageLink)
                            //   : ImageAlt
                            userdetailsData.imageLink
                              ? userdetailsData.imageLink
                              : ImageAlt
                          }
                          style={{ height: "230px", width: "190px" }}
                        ></img>
                      </div>
                    </Col>
                    <Col md={4} lg={4} sm={4}>
                      <div>
                        <p className="cardlabelsCI">Personal Details</p>
                        <hr />
                        <Row>
                          <Col md={5}>
                            <p className="pdetailsheading">Full Name</p>
                          </Col>
                          <Col md={1}></Col>
                          <Col md={6}>
                            <p className="pdetailscolor">
                              {userdetailsData.fullName
                                ? userdetailsData.fullName
                                : "N/A"}
                            </p>
                          </Col>
                        </Row>
                        <Row>
                          <Col md={5}>
                            <p className="pdetailsheading">Email Address</p>
                          </Col>
                          <Col md={1}></Col>
                          <Col md={6}>
                            <p className="pdetailscolor">
                              {userdetailsData.Email
                                ? userdetailsData.Email
                                : "N/A"}
                            </p>
                          </Col>
                        </Row>

                        <Row>
                          <Col md={5}>
                            <p className="pdetailsheading">Phone Number</p>
                          </Col>
                          <Col md={1}></Col>
                          <Col md={6}>
                            <p className="pdetailscolor">
                              {userdetailsData.MobilePhone
                                ? userdetailsData.MobilePhone
                                : "N/A"}
                            </p>
                          </Col>
                        </Row>
                        <Row>
                          <Col md={5}>
                            <p className="pdetailsheading">Date of Birth</p>
                          </Col>
                          <Col md={1}></Col>
                          <Col md={6}>
                            <p className="pdetailscolor">
                              {userdetailsData.dob
                                ? userdetailsData.dob
                                : "N/A"}
                            </p>
                          </Col>
                        </Row>
                        <Row>
                          <Col md={5}>
                            <p className="pdetailsheading">Address</p>
                          </Col>
                          <Col md={1}></Col>
                          <Col md={6}>
                            <p className="pdetailscolor">
                              {userdetailsData.address
                                ? userdetailsData.address
                                : "N/A"}
                            </p>
                          </Col>
                        </Row>
                        <Row>
                          <Col md={5}>
                            <p className="pdetailsheading">State</p>
                          </Col>
                          <Col md={1}></Col>
                          <Col md={6}>
                            <p className="pdetailscolor">
                              {" "}
                              {userdetailsData.state
                                ? userdetailsData.state
                                : "N/A"}
                            </p>
                          </Col>
                        </Row>
                        <Row>
                          <Col md={5}>
                            <p className="pdetailsheading">City</p>
                          </Col>
                          <Col md={1}></Col>
                          <Col md={6}>
                            <p className="pdetailscolor">
                              {" "}
                              {userdetailsData.cityName
                                ? userdetailsData.cityName
                                : "N/A"}
                            </p>
                          </Col>
                        </Row>
                      </div>
                    </Col>
                    <Col md={1} lg={1} sm={2}></Col>
                    <Col md={4} lg={4} sm={4}>
                      <div>
                        <p className="cardlabelsCI">Professional Detals</p>
                        <hr />
                        <Row>
                          <Col md={5}>
                            <p className="pdetailsheading">Company Name</p>
                          </Col>
                          <Col md={1}></Col>
                          <Col md={6}>
                            <p className="pdetailscolor">
                              {" "}
                              {userdetailsData.companyName
                                ? userdetailsData.companyName
                                : "N/A"}
                            </p>
                          </Col>
                        </Row>
                        <Row>
                          <Col md={5}>
                            <p className="pdetailsheading">Industry</p>
                          </Col>
                          <Col md={1}></Col>
                          <Col md={6}>
                            <p className="pdetailscolor">
                              {" "}
                              {userdetailsData.industryName
                                ? userdetailsData.industryName
                                : "N/A"}
                            </p>
                          </Col>
                        </Row>

                        <Row>
                          <Col md={5}>
                            <p className="pdetailsheading">Position</p>
                          </Col>
                          <Col md={1}></Col>
                          <Col md={6}>
                            <p className="pdetailscolor">
                              {" "}
                              {userdetailsData.positionName
                                ? userdetailsData.positionName
                                : "N/A"}
                            </p>
                          </Col>
                        </Row>

                        <Row>
                          <Col md={5}>
                            <p className="pdetailsheading">Linkedin</p>
                          </Col>
                          <Col md={1}></Col>
                          <Col md={6}>
                            <p className="pdetailscolor">
                              {userdetailsData.linkedIn
                                ? userdetailsData.linkedIn
                                : "N/A"}
                            </p>
                          </Col>
                        </Row>
                      </div>
                    </Col>
                  </Row>
                </Card>
                <Card>
                  <div>
                    <Row className="mt-2  ">
                      <Col md={3}>
                        <p className="cardlabelsCI userbookingmargin">
                          User Bookings
                        </p>
                      </Col>
                      <Col md={4}></Col>
                      <Col md={5}>
                        <Nav tabs className="nav-tabs-custom nav-justified">
                          <NavItem>
                            <NavLink
                              style={{ cursor: "pointer" }}
                              className={classnames({
                                active: activeTabJustify === "1",
                              })}
                              onClick={() => {
                                toggleCustomJustified("1");
                                // getUseractivebookings();
                              }}
                            >
                              <span className="d-none d-sm-block">Active</span>
                            </NavLink>
                          </NavItem>
                          <NavItem>
                            <NavLink
                              style={{ cursor: "pointer" }}
                              className={classnames({
                                active: activeTabJustify === "2",
                              })}
                              onClick={() => {
                                toggleCustomJustified("2");
                                // fetchScheduledBookings();
                              }}
                            >
                              <span className="d-none d-sm-block">
                                Scheduled
                              </span>
                            </NavLink>
                          </NavItem>
                          <NavItem>
                            <NavLink
                              style={{ cursor: "pointer" }}
                              className={classnames({
                                active: activeTabJustify === "3",
                              })}
                              onClick={() => {
                                toggleCustomJustified("3");
                                // fetchPastBookings();
                              }}
                            >
                              <span className="d-none d-sm-block">Past</span>
                            </NavLink>
                          </NavItem>
                        </Nav>

                        <TabContent activeTab={activeTabJustify}>
                          <TabPane tabId="1" className="p-3"></TabPane>
                          <TabPane tabId="2" className="p-3">
                            {/* <Row>
													<Col sm="12">
														<CardText>
															Food truck fixie locavore,
                          								</CardText>
													</Col>
												</Row> */}
                          </TabPane>
                          <TabPane tabId="3" className="p-3"></TabPane>

                          <TabPane tabId="8" className="p-3">
                            <Row>
                              <Col sm="12"></Col>
                            </Row>
                          </TabPane>
                        </TabContent>
                      </Col>
                    </Row>
                  </div>
                  <div>
                    {/* {userActiveBookings ? ( */}
                    <Row>
                      <Col className="col-12 ">
                        <Card>
                          <CardBody>
                            <PaginationProvider
                              pagination={paginationFactory(pageOptions)}
                              keyField="id"
                              columns={columns}
                              // data={userActiveBookings}
                              data={
                                activeTabJustify === "1"
                                  ? userActivebookingsData
                                  : activeTabJustify === "2"
                                  ? userScheduledBookingsData
                                  : activeTabJustify === "3"
                                  ? userPastBookingsData
                                  : userActivebookingsData
                              }
                            >
                              {({ paginationProps, paginationTableProps }) => (
                                <ToolkitProvider
                                  keyField="id"
                                  columns={columns}
                                  data={
                                    activeTabJustify === "1"
                                      ? userActivebookingsData
                                      : activeTabJustify === "2"
                                      ? userScheduledBookingsData
                                      : activeTabJustify === "3"
                                      ? userPastBookingsData
                                      : userActivebookingsData
                                  }
                                  search
                                >
                                  {(toolkitProps) => (
                                    <React.Fragment>
                                      <Row className="mb-2">
                                        <Col md="4">
                                          <div className="search-box me-2 mb-2 d-inline-block">
                                            <div className="position-relative">
                                              <SearchBar
                                                {...toolkitProps.searchProps}
                                              />
                                              <i className="search-box chat-search-box" />
                                            </div>
                                          </div>
                                        </Col>
                                        <Col md="4"></Col>
                                        <Col md="4"></Col>
                                      </Row>

                                      <Row>
                                        <Col xl="12">
                                          <div className="table-responsive">
                                            <BootstrapTable
                                              hover
                                              keyField={"id"}
                                              responsive
                                              bordered={false}
                                              striped={false}
                                              defaultSorted={defaultSorted}
                                              // selectRow={selectRow}
                                              classes={
                                                "table align-middle table-nowrap"
                                              }
                                              headerWrapperClasses={
                                                "thead-light"
                                              }
                                              {...toolkitProps.baseProps}
                                              {...paginationTableProps}
                                            />
                                          </div>
                                        </Col>
                                      </Row>

                                      <Row className="align-items-md-center mt-3">
                                        <Col className="inner-custom-pagination d-flex">
                                          <span className="paginationitmes ">
                                            Items
                                          </span>
                                          <div className="d-inline">
                                            {/* <SizePerPageDropdownStandalone
                                              {...paginationProps}
                                            /> */}
                                            <ButtonDropdown
                                              isOpen={isOpen}
                                              toggle={toggle}
                                              onClick={(e) => pagelengthnum(e)}
                                            >
                                              <DropdownToggle
                                                caret
                                                color="secondary"
                                              >
                                                {pageSize}
                                              </DropdownToggle>
                                              <DropdownMenu>
                                                <DropdownItem value="1">
                                                  10
                                                </DropdownItem>
                                                <DropdownItem value="2">
                                                  30
                                                </DropdownItem>
                                                <DropdownItem />
                                                <DropdownItem value="3">
                                                  50
                                                </DropdownItem>
                                              </DropdownMenu>
                                            </ButtonDropdown>
                                          </div>
                                          <span className="paginationitmes1 ">
                                            show
                                          </span>
                                          <div className="text-md-right ms-auto">
                                            {/* <PaginationListStandalone
                                                {...paginationProps}
                                              /> */}

                                            <Button
                                              color="secondary"
                                              className="waves-effect "
                                              style={{
                                                marginLeft: 7,
                                                marginRight: 7,
                                              }}
                                              disabled={currentPage <= 1}
                                              onClick={() =>
                                                currentPage === 1
                                                  ? null
                                                  : changeCurrentPage(
                                                      (prev) => prev - 1
                                                    )
                                              }
                                            >
                                              <i className="dripicons-chevron-left"></i>
                                            </Button>

                                            <Button
                                              style={{
                                                marginLeft: 7,
                                                marginRight: 7,
                                              }}
                                              color="success"
                                              className="btn-rounded waves-effect waves-light me-1 mr-2 ml-2"
                                            >
                                              {currentPage}
                                            
                                            </Button>
                                            {activeTabJustify === "1"?
                                            <Button
                                              style={{
                                                marginLeft: 7,
                                                marginRight: 7,
                                              }}
                                              color="secondary"
                                              className="waves-effect"
                                              disabled={!hasNextPage}
                                              onClick={() =>
                                                changeCurrentPage(
                                                  (prev) => prev + 1
                                                )
                                              }
                                            >
                                              <i className="dripicons-chevron-right"></i>
                                            </Button>
                                            :
                                            activeTabJustify === "2"?
                                          
                                            <Button
                                              style={{
                                                marginLeft: 7,
                                                marginRight: 7,
                                              }}
                                              color="secondary"
                                              className="waves-effect"
                                              disabled={!scheduledHasNextPage}
                                              onClick={() =>
                                                changeCurrentPage(
                                                  (prev) => prev + 1
                                                )
                                              }
                                            >
                                              <i className="dripicons-chevron-right"></i>
                                            </Button>
                                            :
                                            activeTabJustify === "3"?
                                            <Button
                                              style={{
                                                marginLeft: 7,
                                                marginRight: 7,
                                              }}
                                              color="secondary"
                                              className="waves-effect"
                                              disabled={!pastHasNextPage}
                                              onClick={() =>
                                                changeCurrentPage(
                                                  (prev) => prev + 1
                                                )
                                              }
                                            >
                                              <i className="dripicons-chevron-right"></i>
                                            </Button>:null}
                                          </div>
                                        </Col>
                                      </Row>
                                    </React.Fragment>
                                  )}
                                </ToolkitProvider>
                              )}
                            </PaginationProvider>
                          </CardBody>
                        </Card>
                      </Col>
                    </Row>
                    {/* ) : null} */}
                  </div>
                </Card>
              </div>
            </Col>
          </Row>
        </div>
      )}
    </>
  );
};

export default UserProfileDetail;
