  const {
    data: {
      data: pastReportsDataa,
      hasNextPage: pastHasNextPage,
      hasPreviousPage: pastHasPreviousPage,
      total: pastTotal,
      refetch: pastReportsRefteh,
    },
    isLoading,
  } = usePaginatedQuery(["pastReprtssData", `${selectedReportType}`], () =>
    getPastreports(token, selectedReportType, postFilter)
  );
  // *************
  // *************
  const {
    data: {
      data: newReportsDataa,
      hasNextPage: newHasNextPage,
      hasPreviousPage: newHasPreviousPage,
      total: newTotal,
      refetch: newReportsRefteh,
    },
  } = usePaginatedQuery(["newReportssData", , `${selectedReportType}`], () =>
    getNewreports(token, selectedReportType, postFilter)
  );
  console.log("pastHasNextPage : ", pastTotal);
  return {
    reportTypeOptions,
    retryFunc,
    downloadFunc,
    pastReportsDataa,
    newReportsDataa,
    newOldReportsData,
    setNewOldReportsData,
    isLoading,
    reportTyperequestFunc,
    pastHasNextPage,
    pastTotal,
    pastHasPreviousPage,
    pastReportsRefteh,
    newHasNextPage,
    newHasPreviousPage,
    newTotal,
    newReportsRefteh,
  };
};

export default useReportRequest;
