import React,{useState} from 'react'
import { Calendar, dateFnsLocalizer } from 'react-big-calendar'
import format from 'date-fns/format'
import parse from 'date-fns/parse'
import startOfWeek from 'date-fns/startOfWeek'
import getDay from 'date-fns/getDay'
import enUS from 'date-fns/locale/en-US'
import DatePicker from 'react-datepicker'
import { Button } from 'bootstrap'
import 'react-big-calendar/lib/css/react-big-calendar.css'
import './css/fullcalenderindex.css'


const locales = {
    'en-US': enUS,
  }

  const localizer = dateFnsLocalizer({
    format,
    parse,
    startOfWeek,
    getDay,
    locales,
  })

const events=[
    {

        title:"Big Meeting",
        allday:true,
        start:new Date('June 20, 2022 11:13:00'),
        end:new Date('June 21, 2022 11:13:00')
},
{
    title:"Vacation",
    start:new Date('June 10, 2022 11:13:00'),
    end:new Date('June 10, 2022 11:13:00')
},
{
    title:"Conferance",
    start:new Date(2022,6,0),
    end:new Date(2022,6,0)
}
]



const FullCalender = () => {

    const [newEvent, setNewEvent]=useState({title:"",start:"",end:""})
    const [ allEvents, setAllEvents]=useState(events)

const handleAllEvents = ()=>{
    setAllEvents([...allEvents, newEvent])
}

  return (
    <div className="page-content">
        <h1>Calendar</h1>
        <h2>Add new bookings</h2>
        <input type="text" placeholder=' Add title' style={{width:"20%", marginRight:"10px"} }
        value= {newEvent.title} onChange={(e)=>setNewEvent({...newEvent,title:e.target.value})} />
      <DatePicker placeholderText='Start Date' style={{marginRight:"10px"}}
      selected={newEvent.start} onChange={(start)=>setNewEvent({...newEvent,start:start.target.value})} />
            <DatePicker placeholderText='End Date' style={{marginRight:"10px"}}
      selected={newEvent.end} onChange={(end)=>setNewEvent({...newEvent,end:end.target.value})} />
      <Button onclick={handleAllEvents} style={{marginTop:"10px"}}>Submit</Button>

       <div>
       <Calendar
      localizer={localizer}
    scrollyx
    events={allEvents}
      startAccessor="start"
      endAccessor="end"
    //   className='cleventheight'
    style={{ height: 500,margin:"50px" }}
    />
       </div>
        </div>
  )
}

export default FullCalender
