     await getPastreports(token,selectedReportType,postFilter);
     
    //  pastReportsRefteh;
    // QueryClient.invalidateQueries("pastReprtssData",selectedReportType);
  },
  [selectedReportType]
);

  return (
    {
        reportTypeOptions,
