import React from "react";

import {
  Row,
  Col,
  TabContent,
  NavLink,
  NavItem,
  Input,
  Button,
  FormGroup,
  Nav,
  TabPane,
  Card,
  CardBody,
  Modal,
  ModalHeader,
  ModalBody,
  ButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  ScheduledNextPage,
} from "reactstrap";
import classnames from "classnames";
import "@vtaits/react-color-picker/dist/index.css";
import "flatpickr/dist/themes/material_blue.css";
import Select from "react-select";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory, {
  PaginationProvider,
  PaginationListStandalone,
  SizePerPageDropdownStandalone,
} from "react-bootstrap-table2-paginator";

import { ToastContainer, toast } from "react-toastify";
import { Link } from "react-router-dom";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import "../../Tables/datatables.scss";
import UseRequestBookings from "./useRequestBookings";

const { SearchBar } = Search;
const Loader = require("react-loader");

const RequestBookings = () => {
  const {
    activeTabJustify,
    setLoaded,
    bookingsToday,
    modal_static,
    setModal_static,
    modal_static1,
    setModal_static1,
    revokeTextArea,
    error,
    setError,
    isLoading,
    tog_static,
    tog_static1,
    toggleCustomJustified,
    handeldiomlocation,
    textareachange,
    fetchRequestsBookings,
    fetchRevokedBookings,
    fetchPendingBookings,
    fetchPastBookings,
    fetchScheduledBookings,
    gettodaybookings,
    delRevokeBookings,
    pagelengthnum,
    locationsData,
    pageSize,
    toggle,
    isOpen,
    pageOptions,
    hasNextPage,
    currentPage,
    pastNextPage,
    ScheduledNextPage,
    pastPreviousPage,
    pastTotal,
    changeCurrentPage,
  } = UseRequestBookings();

  const Loader = require("react-loader");

  const pastcolumns = [
    {
      dataField: "id",
      text: "No.",
      sort: true,
    },
    {
      dataField: "_id",
      text: "Booking ID",
      sort: true,
      formatter: (cell, row) => (
        <Link
          to={`/bookingdetail/${row._id}/${activeTabJustify}`}
          className="link"
        >
          {cell}
        </Link>
      ),
    },
    {
      dataField: "userId",
      text: "Customer ID",
      sort: true,
      formatter: (cell, row) => (
        <Link to={`/checkedincustomerdetail/${row.userId}`} className="link">
          {" "}
          {cell}{" "}
        </Link>
      ),
    },
    {
      dataField: "userName",
      text: "Name",
      sort: true,
    },
    {
      dataField: "businessName",
      text: "Location",
      sort: true,
    },
    {
      dataField: "resourceName",
      text: "Resource",
      sort: true,
    },
    {
      // var Bookingstartend ={fromTime,"-",toTime},
      dataField: "Bookingstartend",
      text: "Booking Start/End",
      sort: true,
    },
    {
      dataField: "bookingType",
      text: "Booking Type",
      sort: true,
    },

    {
      dataField: "invoiceNumber",
      text: "Invoice ID",
      sort: true,
      formatter: (cell, row) => (
        <Link to={`/invoicesdetailpage/${row.invoiceNumber}`} className="link">
          {" "}
          {cell}{" "}
        </Link>
      ),
    },
    {
      dataField: "bookingStatus",
      text: "Status",
      sort: true,
    },
    {
      dataField: "Total Paid",
      text: "Payment Method",
      sort: true,
    },
  ];

  const columns = [
    {
      dataField: "id",
      text: "No.",
      sort: true,
    },
    {
      dataField: "_id",
      text: "Booking ID",
      sort: true,
      formatter: (cell, row) => (
        <Link
          to={`/bookingdetail/${row._id}/${activeTabJustify}`}
          className="link"
        >
          {cell}
        </Link>
      ),
    },
    {
      dataField: "userId",
      text: "Customer ID",
      sort: true,
      formatter: (cell, row) => (
        <Link to={`/checkedincustomerdetail/${row.userId}`} className="link">
          {" "}
          {cell}{" "}
        </Link>
