import React, { useState } from "react";
import {
  Card,
  Col,
  Row,
  TabContent,
  NavLink,
  NavItem,
  Nav,
  TabPane,
  Input,
  CardBody,
  Button,
} from "reactstrap";
import classnames from "classnames";
import Select from "react-select";
import "./css/reportRequest.css";
import BootstrapTable from "react-bootstrap-table-next";
import ToolkitProvider, { Search } from "react-bootstrap-table2-toolkit";
import paginationFactory, {
  PaginationProvider,
} from "react-bootstrap-table2-paginator";
import useReportRequest from "./generateodf/useReportRequest";

const products = [
  {
    id: 1,
    name: "Airi Satou",
    position: "Accountant",
    office: "Tokyo",
    age: "33",
    startdate: "2008/11/28",
    salary: "$162,700",
  },

  {
    id: 2,
    name: "Angelica Ramos",
    position: "Chief Executive Officer (CEO)",
    office: "London",
    age: "47",
    startdate: "2009/10/09",
    salary: "$1,200,000",
  },

  {
    id: 3,
    name: "Ashton Cox",
    position: "Junior Technical Author",
    office: "San Francisco",
    age: "66",
    startdate: "2009/01/12",
    salary: "$86,000",
  },

  {
    id: 4,
    name: "Bradley Greer",
    position: "Software Engineer",
    office: "London",
    age: "41",
    startdate: "2012/10/13",
    salary: "$132,000",
  },

  {
    id: 5,
    name: "Brenden Wagner",
    position: "Software Engineer",
    office: "San Francisco",
    age: "28",
    startdate: "2011/06/07",
    salary: "$206,850",
  },
];

const ReportRequest = () => {
  const {reportTypeOptions}=  useReportRequest()
  const [activeTabJustify, setActiveTabJustify] = useState("1");
  const { SearchBar } = Search;
  const changeCurrentPage = 1;

  const columns1 = [
    {
      dataField: "id",
      text: "Report Type",
      sort: true,
    },
    {
      dataField: "name",
      text: "Brand(s)",
      sort: true,
    },
    {
      dataField: "position",
      text: "Location(s)",
      sort: true,
    },
    {
      dataField: "office",
      text: "Resource Type(s)",
      sort: true,
    },
    {
      dataField: "age",
      text: "Time Slot",
      sort: true,
    },
    {
      dataField: "startdate",
      text: "Date Range",
      sort: true,
    },
    {
      dataField: "salary",
      text: "Retry",
      sort: true,
      formatter: (cell, row ) => (
     
        <div>
          <Button
            className="w-100"
            color="success"
            size="sm"
            block
            outline
            // onClick={() => {
            //   // tog_static(row._id);
            // }}
          >
            Retry
            
          </Button>
        </div>
    
      ),
    },
  ];

  const columns = [
    {
      dataField: "id",
      text: "Report Type",
      sort: true,
    },
    {
      dataField: "name",
      text: "Brand(s)",
      sort: true,
    },
    {
      dataField: "position",
      text: "Location(s)",
      sort: true,
    },
    {
