import React from "react";
import {
  Button,
  Card,
  Col,
  Container,
  FormGroup,
  Input,
  Row,
} from "reactstrap";
import "./css/generatareport.css";
import Select from "react-select";
import { useLocationsListing } from "../Locations/useLocations";
import UseCategoryDetail from "../Inventories/Categories/CategoriesDetail/useCategoryDetail";
import { useResourceType } from "../Inventories/ResourceType/useResourceType";
import { useInventories } from "../Inventories/useInventories";
const Reports = () => {
  const { categoryDropDown } = UseCategoryDetail();
  const { brandData } = useLocationsListing();
  const { locationData } = useInventories();

  return (
    <div className="page-content">
      {console.log("brandData", brandData)}
      {console.log("locationData", locationData)}
      <p className="reporttoplabel">Generate a report</p>
      <Card>
        <Container>
          <Row>
            <Col md={3}>
              <p className="reportlabel1 mt-4 ">Select your report type:</p>
            </Col>
            <Col md={3}></Col>
            <Col md={3}></Col>
            <Col md={3}></Col>
          </Row>

          <Row className="mt-4">
            <Col md={12}>
              <Select
                // value={selectedMulti}
                // isMulti={true}
                // onChange={this.handleMulti}
                classNamePrefix="select2-selection"
              />
            </Col>
          </Row>

          <Row className="mt-4">
            <Col md={3}>
              <p className="reportlabel1">Select a time period</p>
            </Col>
            <Col md={3}></Col>
            <Col md={3}></Col>
            <Col md={3}></Col>
          </Row>

          <Row className="mt-2">
            <Col md={3}>
              <p className="reportlabel2">From</p>
            </Col>
            <Col md={3}>
              <p className="reportlabel2">To</p>
            </Col>
            <Col md={3}>
              <p className="reportlabel2">Time Slot</p>
            </Col>
            <Col md={3}></Col>
          </Row>

          <Row>
            <Col md={3}>
              <FormGroup>
                <Input
                  // format="HH:mm:ss.SSS"
                  type="time"
                  // defaultValue={locationBrandData.startTime}
                  // value="13:00"
                  // onChange={(e) => {
                  //   startTimeFunc(e);
                  // }}
                  id="appt-time"
                />
              </FormGroup>
            </Col>
            <Col md={3}>
              <FormGroup>
                <Input
                  // format="HH:mm:ss.SSS"
                  type="time"
                  // defaultValue={locationBrandData.startTime}
                  // value="13:00"
                  // onChange={(e) => {
                  //   startTimeFunc(e);
                  // }}
                  id="appt-time"
                />
              </FormGroup>
            </Col>
            <Col md={6}>
              <Select
                // value={selectedMulti}
                // isMulti={true}
                // onChange={this.handleMulti}
                classNamePrefix="select2-selection"
              />
            </Col>
          </Row>

          <Row className="mt-4">
            <Col md={3}>
              <p className="reportlabel1">Make your selections</p>
            </Col>
            <Col md={3}></Col>
            <Col md={3}></Col>
            <Col md={3}></Col>
          </Row>

          <Row className="mt-2">
            <Col md={6}>
              <p className="reportlabel2">Diom Brand(s)</p>
            </Col>
            <Col md={6}>
              <p className="reportlabel2">Diom Location(s)</p>
            </Col>
          </Row>

          <Row>
            <Col md={6}>
              <FormGroup className="select2-container  ">
                <Select
                  // onChange={(opt) => handeldiomlocation(opt.value)}
                  isMulti={true}
                  options={brandData}
                  placeholder="Brands"
                  classNamePrefix="select2-selection "
                />
              </FormGroup>
            </Col>
            <Col md={6}>
              <FormGroup className="select2-container  ">
                <Select
                  isMulti={true}
                  options={locationData}
                  placeholder="All Location"
                  classNamePrefix="select2-selection"
                />
              </FormGroup>
            </Col>
          </Row>

          <Row className="mt-4">
            <Col md={12}>
              <p className="reportlabel2">Resource Type(s)</p>
            </Col>
          </Row>

          <Row>
            <Col md={12}>
              <Select
                // value={selectedMulti}
                isMulti={true}
                // onChange={handleMulti}
                options={categoryDropDown}
                // classNamePrefix="select2-selection"
              />
            </Col>
          </Row>

          <Row className="mt-4">
            <Col md={6}>
              <p className="reportlabel2">User Type(by Position)</p>
            </Col>

            <Col md={6}>
              <p className="reportlabel2">User Type(by Industry)</p>
            </Col>
          </Row>

          <Row>
            <Col md={6}>
              <Select
                // value={selectedMulti}
                // isMulti={true}
                // onChange={this.handleMulti}
                classNamePrefix="select2-selection"
              />
            </Col>
            <Col md={6}>
              <Select
                // value={selectedMulti}
                // isMulti={true}
                // onChange={this.handleMulti}
                classNamePrefix="select2-selection"
              />
            </Col>
          </Row>

          <Row className="mt-5">
            <Col md={3}></Col>
            <Col md={5}></Col>
            <Col md={2}>
              <Button
                color="success"
                outline
                className="invoicesdetailbuttons waves-effect waves-light  w-100  "
              >
                Clear All
              </Button>
            </Col>
            <Col md={2}>
              <Button
                color="success"
                className="invoicesdetailbuttons waves-effect waves-light  w-100  "
              >
                Export
              </Button>
            </Col>
          </Row>

          <p></p>
        </Container>
      </Card>
    </div>
  );
};

export default Reports;
